import React, { useState, useEffect, useRef } from "react";
import { Button, Image, View, Platform } from "react-native";
import * as ImagePicker from "expo-image-picker";
import { MaterialCommunityIcons } from "@expo/vector-icons";
// import { firebase_storage_ref } from "../database/Firebase";
import firebase from "../database/Firebase";
import "firebase/storage";
import Constants from "expo-constants";

export default function ProductImg(props) {
  //Initialize firebase

  const dummyImg =
    "https://www.lankabangla.com/wp-content/uploads/2019/08/no_image_png_935227.png";
  const [image, setImage] = useState(dummyImg);

  useEffect(() => {
    (async () => {
      if (Platform.OS !== "web") {
        const {
          status,
        } = await ImagePicker.requestMediaLibraryPermissionsAsync();
        if (status !== "granted") {
          alert("Sorry, we need camera roll permissions to make this work!");
        }
      }
    })();
  }, []);

  const pickImage = async () => {
    let result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.All,
      allowsEditing: true,
      aspect: [4, 3],
      quality: 1,
    });

    if (!result.cancelled) {
      setImage(result.uri);
      uploadImg(result.uri).then(() => {
        firebase
          .storage()
          .ref()
          .child("files")
          .getDownloadURL()
          .then((url) => {
            console.log(url);
          });
        console.log("Success");
      }).catch = (err) => {
        console.log("Error in uploading to firebase", err);
      };
      if (result.uri != dummyImg) {
        props.onImgAdded(result.uri);
      } else props.onImgAdded(dummyImg);
    }
  };
  const uploadImg = async (uri) => {
    let response = await fetch(uri);
    let blob = await response.blob();
    let ref = firebase
      .storage()
      .ref()
      .child("images/" + "files");
    return ref.put(blob);
  };

  return (
    <>
      <View style={{ flex: 1, alignItems: "center", justifyContent: "center" }}>
        <View style={{ flexDirection: "row" }}>
          <Image
            source={{ uri: image }}
            style={{ width: 200, height: 200 }}
            onClick={() => setImage(dummyImg)}
          />
          <MaterialCommunityIcons
            onPress={pickImage}
            name="plus-box"
            size={24}
            color="black"
            style={{ position: "absolute", bottom: 0 }}
          />
        </View>
      </View>
    </>
  );
}
