import React, { useState, useCallback } from "react";
import axios from "axios";
import { Card } from "react-native-paper";
import {
  View,
  Image,
  ScrollView,
  StyleSheet,
  Text,
  ActivityIndicator,
} from "react-native";
import { AntDesign } from "@expo/vector-icons";
import { useFocusEffect } from "@react-navigation/native";
import TopBar from "../components/TopBar";
import config from "../../config";
import Store from "./Store";

export const FeedContext = React.createContext();

export default function Content() {
  const [content, setContent] = useState([]);
  const [open, setOpen] = useState(false);
  let load = false;

  if (content.length == 0) {
    load = true;
  }

  const getData = () => {
    axios({
      url: `${config.URI}/posts/`,
    })
      .then((res) => {
        console.log("Check out data");
        const data = res.data;
        setContent(data);
      })
      .catch(() => {
        console.log("Error in retriving data..");
      });
  };

  const fetchStores = () => {
    axios({
      url: `${config.URI}/profile/stores${searchQuery}`,
    })
      .then((res) => {
        console.log("Check out your fav store");
        const data = res.data;
        if (data.length > 0) {
          setStoresList(data);
        }
        setTemp(searchQuery);
        setSearchQuery("");
      })
      .catch(() => {
        console.log("Error in retriving data..");
      });
  };

  const LeftContent = () => (
    <AntDesign name="rightcircle" size={26} color="black" />
  );

  const openProfile = (store) => {
    setOpen(true);
    fetchStores();
    setContent([]);
  };

  useFocusEffect(
    useCallback(() => {
      getData();

      return () => console.log("Stopped Fetching");
    }, [])
  );

  return (
    <>
      <View>
        <TopBar name="Feed" />
      </View>

      {load && (
        <View style={styles.loading}>
          <ActivityIndicator size="large" color="#db7093" />
        </View>
      )}

      {open && <Store />}
      <ScrollView>
        {content.map((item) => (
          <Card key={item._id} onPress={() => openProfile(item.store)}>
            <Card.Title
              title={item.store}
              subtitle={item.name}
              left={LeftContent}
            />
            <Card.Cover
              source={{ uri: item.image }}
              style={{ resizeMode: "contain" }}
            />
          </Card>
        ))}
      </ScrollView>
    </>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingTop: 65,
    backgroundColor: "white",
    justifyContent: "center",
    alignItems: "center",
  },
  loading: {
    flex: 1,
    justifyContent: "center",
    alignContent: "center",
    alignItems: "center",
  },
});
