import { AntDesign } from "@expo/vector-icons";
import { StyleSheet, Text, View, TouchableOpacity } from "react-native";
import React, { useState } from "react";

const Header = () => {
  const [reg, setReg] = useState(false);
  const [log, setLog] = useState(true);
  return (
    <>
      <View style={styles.icon}>
        <AntDesign name="instagram" size={30} color="black" />
        <Text style={styles.header}>
          {" "}
          Insta<Text style={{ color: "#db7093" }}> Stores </Text>
        </Text>
      </View>
      <View style={styles.buttonWrapper}>
        <TouchableOpacity
          style={styles.saveButton}
          onPress={() => {
            setReg(!reg);
            setLog(false);
          }}
        >
          <Text style={styles.saveButtonText}>Join New</Text>
        </TouchableOpacity>
        <TouchableOpacity
          style={styles.saveButton}
          onPress={() => {
            setReg(false);
            setLog(!log);
          }}
        >
          <Text style={styles.saveButtonText}>Member !</Text>
        </TouchableOpacity>
      </View>
    </>
  );
};

const styles = StyleSheet.create({
  header: {
    fontSize: 25,
    textAlign: "center",
    margin: 10,
    fontWeight: "bold",
  },
  icon: {
    flexDirection: "row",
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: "#F5FCFF",
    // marginTop: "10%",
    marginBottom: "10%",
  },
  buttonWrapper: {
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "center",
  },
  saveButton: {
    borderWidth: 3,
    // borderColor: "#bc8f8f",
    backgroundColor: "black",
    color: "#db7093",
    padding: 10,
    alignItems: "center",
    // marginTop: 5,
    marginBottom: 20,
    marginRight: 10,
  },

  saveButtonText: {
    color: "#db7093",
    fontSize: 20,
    textAlign: "center",
  },
});

export default Header;
