import { StatusBar } from "expo-status-bar";
import React, { useEffect, useState } from "react";
import AsyncStorage from "@react-native-async-storage/async-storage";
import Register from "./src/screens/Register";
import SwitchTabs from "./src/navigations/SwitchTabs";
import Main from "./src/screens/Main";
import UserContext from "./src/components/Context";

export default function App() {
  const [data, setData] = useState(null);
  const [flag, setFlag] = useState(false);

  // useEffect(() => {
  //   AsyncStorage.getItem("credentials").then((res) => {
  //
  //     setData(res[0]);
  //     console.log("Response", res[0]);
  //   });

  // }, []);
  useEffect(() => {
    (async () => {
      let res = await AsyncStorage.getItem("credentials");
      res = JSON.parse(res);
      setData(res[0]);
      if (res != null) setFlag(true);
    })();
  }, []);

  // if (data) {
  //   return <Main />;
  // }
  return (
    <>
      {!flag && (
        <UserContext.Provider value={[data, setData]}>
          <SwitchTabs />
        </UserContext.Provider>
      )}

      {flag && <Main />}
    </>
  );
}
