import React, { useEffect, useState } from "react";
import {
  StyleSheet,
  Text,
  View,
  TextInput,
  ScrollView,
  TouchableOpacity,
} from "react-native";
import axios from "axios";
import AsyncStorage from "@react-native-async-storage/async-storage";
import Content from "./Content";
import config from "../../config";
import ProductImg from "../components/ChoosePic";
import TopBar from "../components/TopBar";

export default function Post({ navigation }) {
  const initialState = { name: "", price: "", image: "" };

  const [product, setProduct] = useState(initialState);
  const [isPosted, setIsPosted] = useState(false);
  const [phone, setPhone] = useState("");
  const [store, setStore] = useState("");

  useEffect(() => {
    AsyncStorage.getItem("credentials").then((res) => {
      res = JSON.parse(res);
      setPhone(res[0].mobile);
      getStores(res[0].mobile);
    });
  }, []);

  const getStores = (mobile) => {
    axios({
      url: `${config.URI}/profile/${mobile}`,
    })
      .then((res) => {
        console.log("Check out data");
        const data = res.data;
        setStore(data.companyName);
      })
      .catch(() => {
        console.log("Error in retriving data..");
      });
  };

  const handleSave = () => {
    const payload = {
      name: product.name,
      price: product.price,
      image: product.image,
      mobile: phone,
      store: product.store,
    };
    axios({
      url: `${config.URI}/posts/upload`,
      method: "POST",
      data: payload,
    })
      .then(() => {
        console.log("Added a new product.");
        setIsPosted(true);
        setProduct(initialState);
        navigation.navigate("Profile");
      })
      .catch((error) => {
        console.log("Internal server error.", error);
      });
  };

  const onImgAdded = (res) => {
    setProduct({ ...product, image: res });
  };

  return (
    <>
      <View>
        <TopBar name="New Post" />
      </View>
      <View style={styles.container}>
        <ScrollView>
          <View>
            <TextInput
              maxLength={20}
              placeholder="Enter Product Name"
              value={product.name}
              style={styles.inputContainer}
              onChangeText={(text) => setProduct({ ...product, name: text })}
            />
            <TextInput
              maxLength={20}
              placeholder="Enter price"
              value={product.price}
              style={styles.inputContainer}
              onChangeText={(text) => setProduct({ ...product, price: text })}
            />

            {/* <TextInput
              maxLength={20}
              placeholder="Enter store"
              value={product.store}
              style={styles.inputContainer}
              
            /> */}
          </View>
          <View>
            <ProductImg onImgAdded={onImgAdded} isPosted={isPosted} />
          </View>
          <View>
            <TouchableOpacity style={styles.saveButton} onPress={handleSave}>
              <Text style={styles.saveButtonText}>Save</Text>
            </TouchableOpacity>
          </View>
        </ScrollView>
      </View>
    </>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingTop: 65,
  },
  header: {
    fontSize: 25,
    textAlign: "center",
    margin: 10,
    fontWeight: "bold",
  },
  inputContainer: {
    margin: 10,
    borderBottomWidth: 1,
    borderColor: "grey",
  },

  saveButton: {
    borderWidth: 2,
    borderColor: "black",
    backgroundColor: "#db7093",
    padding: 15,
    alignItems: "center",
    margin: 5,
  },
  saveButtonText: {
    color: "#FFFFFF",
    fontSize: 20,
    textAlign: "center",
  },
});
